INSERT INTO `dn_company` VALUES ('7', null, '公司简介', '公司简介', '<p>\r\n	<span style=\"color:#000000;\">&nbsp; &nbsp; </span><span style=\"color:#000000;\">北京远达海科技有限公司是工程、矿山整机及配件销售的专业化公司。公司的服务宗旨：为客户提高生产效率、降低生产成本。公司拥有一批在工程机械领域工作多年、具有丰富经验的工程师，为客户在工程机械运行方面排忧解难，为客户的设备高效、环保、安全的运行保驾护航。能为客户提供安全、可靠的产品是我公司全体员工的心愿。</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</span><img src=\"/library/kindeditor/attached/image/20180807/20180807165532_90559.jpg\" alt=\"\" /><br />\r\n<span style=\"color:#000000;\"> &nbsp; &nbsp;&nbsp;公司成立以来，与国内外主要工程机械生产厂家如：山特维克、山推、小松、日立、三一、中联、鼎盛、重汽、徐工、柳工、等建立了良好的供应关系，保证了所供配件的品质及准确性。涉及的产品广泛应用于公路、水利、铁路、路桥、石油、化工、电力、冶金等机械行业。近年来，我公司成立了国际贸易部，为进口工程机械配件在国内的销售提供了保障。</span><br />\r\n<span style=\"color:#000000;\"> &nbsp; &nbsp; 我们主要服务于国内企业及国内企业在海外施工的单位，并正在逐步深入该领域，将我们成熟的专业技术、管理和服务继续引入这个具有很大潜力的市场。多年以来，始终把客户放在第一位，以热情、优质、高效的服务，赢得了中地海外集团、中铁集团、中国水利水电集团、冀东水泥、中油管道局、中地国际工程公司等客户的广泛赞誉。通过努力，公司建立起了覆盖海内外主要市场的贸易网络,已经应客户的需求向坦桑尼亚、塞内加尔、尼日利亚、马里、埃塞俄比亚、马达加斯加、摩洛哥等项目部供应工程机械配件。目前，公司继续扩大出口工程机械配件总体规划，结合我国政府提出“走出去”的发展战略，在原出口工程机械配件的基础上，结合当地市场的需求，不断扩大新的工程机械配件领域和探寻新的贸易方式。</span>\r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;\">&nbsp;&nbsp;&nbsp;&nbsp;进入二十一世纪以来，我们按照国际惯例，遵循平等的原则、多赢的规则，严格遵守契约，获取“阳光下的利润”抓住机遇，通过大力实施业务创新和管理创新，转变经营模式和经营内涵，培育新的核心能力，努力成为工程机械配件市场综合服务商</span><br />\r\n<span style=\"color:#000000;\"> 随着多年的发展和积累，已经培植了广泛的市场，将竭诚同海内外朋友合作，共同建设美好的未来。</span><br />\r\n<span style=\"color:#000000;\"> 我们代理销售的工程机械配件：</span><br />\r\n<span style=\"color:#000000;\"> 1、山特维克、壳牌润滑油。</span><br />\r\n<span style=\"color:#000000;\"> 2、山推、宣工大马力推土机整机、零部件。</span><br />\r\n<span style=\"color:#000000;\"> 3、小松、日立、VOLVO、卡特等挖掘机整机及配件。</span><br />\r\n<span style=\"color:#000000;\"> 4、国产：潍柴、上柴；进口：康明斯、道依兹等品牌发动机配件。</span><br />\r\n<span style=\"color:#000000;\"> 5、重汽、斯太尔、东风、解放、福田等重型汽车配件。</span><br />\r\n<span style=\"color:#000000;\"> 本公司全体员工将以更加细致、周到、专业的服务与您共创美好未来！</span>\r\n</p>', '1460366433', '1535942995', 'Company introduction', '<p class=\"MsoNormal\">\r\n	&nbsp; &nbsp; Daqing Dannuo Petroleum\r\nTechnology Development Co., Ltd. is located inDaqing City,HeilongjiangProvince,China. Boasting\r\nan investment of RMB 300 million and covering an area of 33,230 square meters,\r\nthe company is engaged in R&amp;D and production of non-beam pumping unit, permanent\r\nmagnet direct drive screw pump, linear motor oil pump, other mechanical\r\nequipment as well as permanent magnet motor energy saving transformation. It is\r\nalso responsible for oil drilling technology development service .\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	&nbsp; &nbsp; The\r\nelectronically-controlled directional non-beam pumping unit independently\r\ndeveloped by Daqing Dannuo Petroleum Technology Development Co., Ltd. features\r\nsuch mechanical properties as simple structure, basically maintenance-free,\r\neasy operation, long stroke, low frequency of stroke and large load; by the use\r\nof efficient direct-drive permanent magnet motor, it is able to conduct\r\nintelligent intermittent pumping in accordance with different underground\r\nworking conditions, thus saving energy by 30% -50%; also with remote control\r\nand remote alarm functions, it is the most ideal and most applicable machine\r\nfor oilfield digitalization, mining automation and intensive management.\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;\">\r\n	<img src=\"/library/kindeditor/attached/image/20160512/20160512110746_60188.png\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>');
INSERT INTO `dn_company` VALUES ('8', null, '企业文化', '企业文化', '<p>\r\n	<span style=\"font-size:14px;font-family:SimSun;\">创造优质品牌、铸就一流企业</span><br />\r\n<span style=\"font-size:14px;font-family:SimSun;\"> 打造绝对竞争力的产品</span><br />\r\n<span style=\"font-size:14px;font-family:SimSun;\"> 缔造凝聚力如一的团队</span><br />\r\n<span style=\"font-size:14px;font-family:SimSun;\"> 诚信立业满足用户需求</span><br />\r\n<span style=\"font-size:14px;font-family:SimSun;\"> 质量为先解决后顾之忧</span><br />\r\n<span style=\"font-size:14px;font-family:SimSun;\"> 满足客户需求是我们永恒的追求</span> \r\n</p>\r\n<p style=\"font-size:14px;color:#666666;\">\r\n	<img src=\"/library/kindeditor/attached/image/20180807/20180807154325_97162.jpg\" alt=\"\" />\r\n</p>\r\n<img src=\"file://C:/Users/ThinkPad/Desktop/%E7%9F%B3%E6%B2%B9/img/wenhuaimg01.png\" />', '1460366478', '1533627809', 'Enterprise culture', '<p>\r\n	<span style=\"font-size:16px;\"><strong>Leading Technology, Ultimate\r\nManagement, Best Products and Highest Credit</strong></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	Building quality brand and\r\ncreating first-class companies\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	Creating competitive products\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	Building a cohesive team\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	Meeting user needs through\r\nfine business\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	Manufacturing reliable\r\nproducts of high quality\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span>Customer satisfaction is the eternal pursuit.</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<img src=\"http://www.dannuoshiyou.com/library/kindeditor/attached/image/20160414/20160414184253_98503.png\" alt=\"\" /> \r\n</p>');
