INSERT INTO `dn_company` VALUES ('10', null, '企业发展', '企业发展', '<p>\r\n	<br />\r\n</p>\r\n<div class=\"textLeft\">\r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<h5>\r\n	<span style=\"font-size:18px;\">1987年 起航</span> \r\n</h5>\r\n<p>\r\n	><span style=\"line-height:1.5;\">蒋少炳先生创建北京奇盛兴远国际贸易有限公司！</span> \r\n</p>\r\n<h5 class=\"later02\">\r\n	<span style=\"font-size:18px;\">2009年 成长</span> \r\n</h5>\r\n<p>\r\n	><span style=\"line-height:1.5;\">德州办事处、潍坊办事处正式成立！</span> \r\n</p>\r\n<h5 class=\"later\">\r\n	<span> \r\n	<h1>\r\n		<span style=\"font-size:18px;\">2011</span><span style=\"font-size:18px;\">年 亮剑</span> \r\n	</h1>\r\n</span> \r\n</h5>\r\n<p>\r\n	><span style=\"line-height:1.5;\">大庆丹诺石油科技开发有限公司正式成立！</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span>></span>注册资本贰亿壹仟万！\r\n</p>\r\n<h5 class=\"later04\">\r\n	<span style=\"font-size:18px;\">2012年 精进</span> \r\n</h5>\r\n<p>\r\n	><span style=\"line-height:1.5;\">通过</span><span style=\"line-height:1.5;\">ISO19001</span><span style=\"line-height:1.5;\">质量管理体系认证！</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\"><br />\r\n</span> \r\n</p>\r\n<h5 class=\"later05\">\r\n	<span style=\"font-size:18px;\">201</span><span style=\"font-size:18px;font-weight:normal;line-height:1.5;\">3</span><span style=\"font-size:18px;font-weight:normal;line-height:1.5;\"><strong>年 跨越</strong></span> \r\n</h5>\r\n<p>\r\n	><span style=\"line-height:1.5;\">辽宁办事处正式成立！</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\"> </span> \r\n</p>\r\n<h5 class=\"later05\">\r\n	<span style=\"font-size:18px;\">201</span><span style=\"font-size:18px;font-weight:normal;line-height:1.5;\">4</span><span style=\"font-size:18px;font-weight:normal;line-height:1.5;\"><strong>年 腾飞</strong></span> \r\n</h5>\r\n<p>\r\n	><span style=\"line-height:1.5;\">西安办事处、新疆办事处正式成立！</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\"><br />\r\n</span> \r\n</p>\r\n<h5 class=\"later05\">\r\n	<span s