INSERT INTO `dn_qita` VALUES ('1', '人才理念', '人才理念', '<p style=\"color:#666666;font-size:medium;font-family:Simsun;\">\r\n	人才是企业的资本，人才是最宝贵的财富，北京远达海的大门对所有有志于事业的人敞开着。\r\n</p>\r\n<p style=\"color:#666666;font-size:medium;font-family:Simsun;\">\r\n	公司的用人标准是：具备创新意识和不断进取精神。\r\n</p>\r\n<p style=\"color:#666666;font-size:medium;font-family:Simsun;\">\r\n	公司的竞争机制是：能者上，平者让，庸者下。\r\n</p>\r\n<p style=\"color:#666666;font-size:medium;font-family:Simsun;\">\r\n	公司竭尽所能做到：人适其位，位适其人。\r\n</p>\r\n<p style=\"color:#666666;font-size:medium;font-family:Simsun;\">\r\n	公司不仅通过待遇留人，公司更愿通过事业留人，感情留人。\r\n</p>', '1460287919', '1532489831', 'Personnel recruitment', '<p class=\"MsoNormal\">\r\n	<span style=\"font-size:10px;\">Talent is the capital,\r\ntalent is the most valuable asset, Darrow doors for all interested in the cause\r\nof people open.</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:10px;\">The company\'s employment\r\nstandards are: have the sense of innovation and enterprising spirit.</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:10px;\">The company is a competitive\r\nmechanism: ability&nbsp; people &nbsp;promoted.</span><span> </span><span style=\"font-size:10px;\">Ordinary people demotion.</span><span> </span><span style=\"font-size:10px;\">Disability\r\npeople leaves.</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:10px;\">Companies do everything we\r\ncan do: people fit their position, their position appropriate people.</span> \r\n</p>\r\n<span style=\"font-size:10px;\">The company not only through pay and conditions,\r\ncompanies are more willing to work through the cause of keeping feelings to\r\npeople.</span>');
